import { Component, OnInit } from '@angular/core';
import { DataService } from '../data.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit {
  meals: any[] = []

  constructor(private mealService: DataService) {}

  currentDate: Date = new Date();
  currentMonth!: string;
  currentDay!: string;

  ngOnInit(): void {
    this.currentDate = new Date();
    this.currentMonth = this.mealService.getMonthName(this.currentDate.getMonth());
    this.currentDay = this.mealService.getDayOfWeek(this.currentDate.getDay());

    this.mealService.loadRandomMeal().subscribe(
      data => console.log(data)
    );

    this.loadMeals();
  }

  onEvent(event: any): void {
    // Tu peux traiter l'événement ici si nécessaire
    console.log(event);
  }

  loadMeals(): void{
    this.mealService.getMeals().subscribe(
      data => { this.meals = data;
      });
    }
}
<div>
    <h2>Toutes les recettes</h2>
  <div class="padding-25 bg-color-1">
    <div class="row">
      <div *ngFor="let meal of meals" class="meal-item">
        <img [src]="meal.img" alt="{{ meal.name }}">
        <p>{{ meal.name }}</p>
      </div>
    </div>
  </div>
  <br><br><br><br><br>
</div>



<!-- home.component.html -->
<div class="padding-25 bg-color-1">
  <div class="row">
    <div class="col-12 d-flex justify-content-center f-large padding-b-25">
      <span class="titre">Tous les plats</span>
    </div>
    <div class="row">
      <ng-container *ngIf="meals.length > 0">
        <div class="col-3" *ngFor="let item of meals">
          <app-meals [meals]="item" (eventOut)="onEvent($event)"></app-meals>
        </div>
      </ng-container>
    </div>
  </div>
</div>
