// category.component.ts
import { Component, OnInit } from '@angular/core';
import { DataService } from '../data.service';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit {
  categories: any[] = [];
  logoPath: string = 'assets/Beef.png'

  constructor(private mealService: DataService) {}

  ngOnInit(): void {
    this.loadCategories();
  }

  loadCategories(): void {
    this.mealService.getCategories().subscribe(data => {
      this.categories = data.categories;
    });
  }
}
<!-- category.component.html -->
<div class="padding-25 bg-color-1">
  <div class="row">
    <div class="col-12 d-flex justify-content-center f-large padding-b-25">
      <span class="titre">Catégories</span>
    </div>
    <div class="row">
      <ng-container *ngIf="categories.length > 0">
        <div class="col-3" *ngFor="let category of categories">
          <div class="category-item">
            <img src="logoPath" class="image">
            <p>{{ category.strCategory }}</p>
          </div>
        </div>
      </ng-container>
    </div>
  </div>
  <br><br><br><br><br><br><br>
</div>
